    private String focusOn = "premise";
    public static String[] formula;
    public static boolean[] value;
    public static Creater c;

    private void AnswerButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_AnswerButtonActionPerformed
        String premise = PremiseTextField.getText();
        String conclusion = ConclusionTextField.getText();
        CheckValidity cv = new CheckValidity(premise, conclusion);
        if (cv.isValid()) {
            c = new Creater(premise, conclusion);
            c.createTree();
            DrawTreeFour dt = new DrawTreeFour();
            dt.setVisible(true);
        } else {
            formula = cv.getFormul();
            value = cv.getValue();
            Unsatisfiable u = new Unsatisfiable();
            u.setVisible(true);
        }
    }//GEN-LAST:event_AnswerButtonActionPerformed

