    private void PButtonMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_PButtonMouseClicked
        if (focusOn.equals("premise")) {
            String text = PremiseTextField.getText();
            text = text + "p";
            PremiseTextField.setText(text);
        } else {
            String text = ConclusionTextField.getText();
            text = text + "p";
            ConclusionTextField.setText(text);
        }
    }//GEN-LAST:event_PButtonMouseClicked

